import React, { useEffect, useState } from 'react'
import { useSelector,useDispatch } from "react-redux"


function SinglePage() {

  const [id, setId]=useState([])
  const dispatch = useDispatch()


  const selector = useSelector(state => state.singleReducer)


// http://www.omdbapi.com/?i=${id}&apikey=34992f21

  const func =()=>{
    setId(selector.find(a=>a.imdbID)) 
    console.log(id)
  }
  func()




