import React, { useEffect, useState } from 'react'
import axios from 'axios';
// https://www.omdbapi.com/?s=star%20wars&apikey=34992f21
function Movies() {

    const [movie, setMovie] = useState([])
    const [name, setName] = useState('')
    useEffect(() => {
        axios.get("https://fakestoreapi.com/products")

            .then(res => res.data)
            .then(res => setMovie(res))

    }, [])




    const handleSubmit = (name) => {
        setMovie([...movie.filter(a=>a == name)]) 
    }
    console.log(movie)


    return (
        <div>
            <div className='main'>
                <h1>Search for movie, TV series ..</h1>
                <input type="text" className='searchInput' onChange={(e) => setName(e.target.value)} />
                <button className='btn' onClick={handleSubmit}>Search</button>

            </div>


             {
               movie.filter((a)=>a.title.toLowerCase().includes(setMovie)).map((a,b)=>(
                   <div key={b}>
                    <h1>
                        {a.title}
                    </h1>
                    <h3>
                        {a.price}
                    </h3>
                    </div>

                ))
              }

        </div>
    )
}

export default Movies
