import './App.css';
import Movie from './components/Movies';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Error from './components/Error';
import SinglePage from './components/SinglePage';
import axios from 'axios';
import React, { useEffect } from 'react'
import { useDispatch } from "react-redux"
import Header from './components/Header';
import Home from './components/Home';





function App() {

  const image = "https://image.tmdb.org/t/p/w1280"

  const search = "https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query="
  const api = "https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=04c35731a5ee918f014970082a0088b1&page=1"

  const dispatch = useDispatch()

  useEffect(() => {
    axios.get("https://api.themoviedb.org/3/movie/popular?api_key=${KEY}&language=en-US&page=1")
      .then(res => res.data.results)
      .then(res => dispatch({
        type: "data",
        payload: res
      }))

  }, [])




  return (

    <div className="App">
      <Header />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/movie" element={<Movie />} />
        <Route path="*" element={<Error />} />
        <Route path="/singlePage" element={<SinglePage />} />
      </Routes>
    </div>



  );
}

export default App;
