import React, { useEffect, useState } from 'react'
import axios from 'axios';

function Movies() {

    const [movie, setMovie] = useState([])
    const [name, setName]= useState()
    useEffect(() => {
        axios.get("https://www.omdbapi.com/?s=star%20wars&apikey=34992f21")
            .then(res => res.data)
            .then(res => setMovie(res))

    }, [setMovie])



    const handleChange=(e)=>{

        console.log(e.target.value)
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        handleChange()

    }

    return (
        <div>
            <div className='main'>
                <h1>Search for movie, TV series ..</h1>

                    <input type="text" className='searchInput' onChange={setName(e)}/>
                    <button className='btn' onClick={handleSubmit}>Search</button>

            </div>

            {
                movie.Search && movie.Search.map((index, key) => (
                    <div key={key}>

                        <img src={index.Poster} />
                        <h1>{index.Type}</h1>
                    </div>

                ))
            }
        </div>
    )
}

export default Movies
