import React from 'react'
import { useState, useEffect } from 'react'
import { useSelector, useDispatch } from "react-redux"
import { Link } from "react-router-dom";

import axios from 'axios';



function Home() {
    const [name, setName] = useState('')

    const image = "https://image.tmdb.org/t/p/w1280"

    const selector = useSelector(state => state.allMovieReducer)


    const dispatch = useDispatch()


    const APIKey = "34992f21"

    const url = `http://www.omdbapi.com/?s=${name}&apikey=34992f21`
    const [movie, setMovie] = useState("")


    const handleSubmit = (e) => {
        e.preventDefault()

        setMovie(name)
    }



    useEffect(() => {
        axios.get(`http://www.omdbapi.com/?s=${name}&apikey=34992f21`)
            .then(res => res.data)
            .then(res => dispatch({
                type: "data",
                payload: res
            }))

    }, [movie])

    console.log()







    return (
        <div>
            <div className='main'>
                <form onSubmit={handleSubmit}>
                    <h1>Search for movie, TV series ..</h1>
                    <input type="text" className='searchInput' value={name} onChange={(e) => setName(e.target.value)} />
                    <button className='btn'>Search</button>

                </form>
            </div>

            <div className="Movie_main">
                {

                    // selector[1] && selector[1].map((a, b) => (
                    //     <div key={b}>
                    //         {a.Year}
                    //     </div>
                    // ))

                }
            </div>



        </div>
    )
}

export default Home
