import React from 'react'
import { useState , useEffect} from 'react'
import { useSelector, useDispatch } from "react-redux"
import { Link } from "react-router-dom";

import axios from 'axios';



function Home() {
    const [name, setName] = useState('')

    const image = "https://image.tmdb.org/t/p/w1280"

    const selector = useSelector(state => state.allMovieReducer)
    const search = ""


    const dispatch = useDispatch()


    const APIKey = "34992f21"

    const url = `http://www.omdbapi.com/?s=${name}&apikey=34992f21`


    const handleSubmit = (e) => {
        e.preventDefault()
    }



    useEffect(() => {
        axios.get(`https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query=${name}`)
            .then(res => res.data)
            .then(res => dispatch({
                type: "data",
                payload: res
            }))

    }, [name])

    console.log(selector)







    return (
        <div>
            <div className='main'>
                <form onSubmit={handleSubmit}>
                    <h1>Search for movie, TV series ..</h1>
                    <input type="text" className='searchInput' value={name} onChange={(e) => setName(e.target.value)} />
                    <button className='btn'>Search</button>

                </form>
            </div>

            <div className="Movie_main">
            {
                    selector[0] && selector.map((a, b) => (
                        <div key={b} className="movie-main-inside">
                            <Link to="singlePage"> <img src={image + a.poster_path} onClick={() => dispatch({
                                type: "moreDetail",
                                payload: a
                            })} /></Link>
                            <button className='more-details' onClick={() => dispatch({
                                type: "moreDetail",
                                payload: a
                            })}><Link className='link' to="/singlePage">More details</Link></button>
                        </div>
                    ))
                }
            </div>



        </div>
    )
}

export default Home
