import React, { useEffect } from 'react'
import { useSelector } from "react-redux"


function SinglePage() {




  const selector = useSelector(state => state.singleReducer)


  useEffect(() => {
    console.log(selector)
    selector && axios.get(`http://www.omdbapi.com/?i=${selector}&apikey=34992f21`)
      .then(res => res.data)
      .then(response => 
        {
          console.log()
        }
        // setMovies(response.Search)
        )
  }, [])

  // http://www.omdbapi.com/?i=${id}&apikey=34992f21

  return (
    <div>

      {
        selector.map((a, b) => (
          <div className='single-main' key={b}>
            <div className='single-main-left'>
              <img src={a.Poster} />
            </div>
            <div className='single-main-right'>
              <h1>{a.Title}</h1>
              <table>
                <tbody>
                  <tr>
                    <td><b> Type: </b> {a.Type}</td>
                  </tr>
                  <tr>
                    <td><b>Year: </b> {a.Year}</td>
                  </tr>
                  <tr>
                    <td><b>imdbID: </b> {a.imdbID}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        ))
      }

    </div>

  )
}

export default SinglePage
