import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useSelector } from "react-redux"

function SinglePage() {

  const [id, setId]=useState('')

  const selector = useSelector(state => state.singleReducer)

  const SearchApi =`http://www.omdbapi.com/?i=${id}&apikey=34992f21`

  const func =()=>{
    selector.map((a,b)=>{
      console.log(a)
    })
  }
  func()


  useEffect(() => {
    axios.get(SearchApi)
        .then(res => res.data)
        .then(response => console.log(response))

     }, [id])




  return (
    <div>

      {
        selector.map((a, b) => (
          <div className='single-main' key={b}>
            <div className='single-main-left'>
              <img src={a.Poster} />
            </div>
            <div className='single-main-right'>
              <h1>{a.Title}</h1>
              <table>
                <tbody>
                  <tr>
                    <td><b> Type: </b> {a.Type}</td>
                  </tr>
                  <tr>
                    <td><b>Year: </b> {a.Year}</td>
                  </tr>
                  <tr>
                    <td><b>imdbID: </b> {a.imdbID}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        ))
      }

    </div>

  )
}

export default SinglePage
