import React from 'react'

function Search() {
    const [name, setName] = useState('')
    const [movie, setMovie] = useState([])
    const image = "https://image.tmdb.org/t/p/w1280"

    const selector = useSelector(state => state.allMovieReducer)



    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(name)
    }
  return (
    <div>
             <div className='main'>
                <form onSubmit={handleSubmit}>
                    <h1>Search for movie, TV series ..</h1>
                    <input type="text" className='searchInput' onChange={(e) => setName(e.target.value)} />
                    <button className='btn'>Search</button>

                </form>
            </div>

            {
                selector[0] && selector[0].filter((a) => a.title.toLowerCase().includes(setMovie)).map((a, b) => (
                    <div key={b}>

                        <img src={image + a.poster_path} />
                    </div>
                ))
            }
    </div>
  )
}

export default Search
