import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { Link } from "react-router-dom";
import {useSelector, useDispatch} from "react-redux"
// featured:"https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=04c35731a5ee918f014970082a0088b1&page=1"
function Movies() {
    const image = "https://image.tmdb.org/t/p/w1280"
    const search="https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query="


    const [movie, setMovie] = useState([])
    const [name, setName] = useState('')
    const selector = useSelector(state=>state)
    useEffect(() => {
        axios.get(" https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=04c35731a5ee918f014970082a0088b1&page=1")
            .then(res => res.data.results)
            .then(res => setMovie(res))

    }, [])

    console.log(movie)

    const handleSubmit = (e) => {
        e.preventDefault()
        axios.get(search + name)
        .then(res => res.data.results)
        .then(res => setMovie(res))

    }


    return (
        <div>
            <div className='main'>
                <form onSubmit={handleSubmit}>
                    <h1>Search for movie, TV series ..</h1>
                    <input type="text" className='searchInput' onChange={(e) => setName(e.target.value)} />
                    <button className='btn'>Search</button>

                </form>
            </div>


            <div className="Movie_main">
                {
                    movie.map((a, b) => (
                        <div key={b} className="movie-main-inside">
                            <Link to="singlePage"> <img src={image + a.poster_path} /></Link>
                            <button className='more-details'><Link className='link' to="singlePage">More details</Link></button>
                        </div>
                    ))
                }
            </div>

        </div>
    )
}

export default Movies
