import { Link } from "react-router-dom";
import {useSelector, useDispatch} from "react-redux"
import React, { useEffect, useState } from 'react'


function Movies() {
    const image = "https://image.tmdb.org/t/p/w1280"
  const search="https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query="
    const [name, setName] = useState('')
    const dispatch = useDispatch()


    const selector = useSelector(state=>state.allMovieReducer)
    console.log(selector)




    return (
        <div>
            <div className='main'>
                <form >
                    <h1>Search for movie, TV series ..</h1>
                    <input type="text" className='searchInput' onChange={(e) => setName(e.target.value)} />
                    <button className='btn'>Search</button>

                </form>
            </div>


            <div className="Movie_main">
                {
                    selector[0]&&selector[0].map((a, b) => (
                        <div key={b} className="movie-main-inside">
                            <Link to="singlePage"> <img src={image + a.poster_path} /></Link>
                            <button className='more-details'  onClick={dispatch({
                                type: "moreDetail"
                            })}><Link className='link' to="singlePage">More details</Link></button>
                        </div>
                    ))
                }
            </div>


        </div>
    )
}

export default Movies
