import React, { useState } from 'react'
import { useSelector } from "react-redux"




function Header() {

    const [name, setName] = useState('')
    const image = "https://image.tmdb.org/t/p/w1280"

    const selector = useSelector(state => state.allMovieReducer)

    const handleSubmit = (e) => {
        e.preventDefault()
        console.log(name)
    }
    return (
        <div>
            <div className='main'>
                <form onSubmit={handleSubmit}>
                    <h1>Search for movie, TV series ..</h1>
                    <input type="text" className='searchInput' onChange={(e) => setName(e.target.value)} />
                    <button className='btn'>Search</button>

                </form>
            </div>

            {
                selector && selector.filter((a) => a.title.toLowerCase().includes(setName)).map((a, b) => (
                    <div key={b}>

                        <img src={image + a.poster_path} />
                    </div>
                ))
            }
        </div>


    )
}

export default Header
