import React from 'react'
import { useState, useEffect }from 'react'
import { useSelector,useDispatch } from "react-redux"
import { Link } from "react-router-dom";


function Home() {
    const [name, setName] = useState('')

    const image = "https://image.tmdb.org/t/p/w1280"

    const search = "https://api.themoviedb.org/3/search/movie?&api_key=04c35731a5ee918f014970082a0088b1&query="

    const selector = useSelector(state => state.allMovieReducer)
    const dispatch = useDispatch()

    useEffect(() => {
        axios.get(sear)
          .then(res => res.data.results)
          .then(res => dispatch({
            type: "data",
            payload: res
          }))

      }, [])

    const handleSubmit = (e) => {
        e.preventDefault()  
    }


    return (
        <div>
            <div className='main'>
                <form onSubmit={handleSubmit}>
                    <h1>Search for movie, TV series ..</h1>
                    <input type="text" className='searchInput' value={name} onChange={(e) => setName(e.target.value.toLowerCase())} />
                    <button className='btn'>Search</button>

                </form>
            </div>

            <div className="Movie_main">
            {

                selector[0].filter((a)=>a.title.toLowerCase().includes(name)).map((a,b)=>(
                     <div key={b} className="movie-main-inside">
                            <Link to="singlePage"> <img src={image + a.poster_path} onClick={() => dispatch({
                                type: "moreDetail",
                                payload: a
                            })} /></Link>
                            <button className='more-details' onClick={() => dispatch({
                                type: "moreDetail",
                                payload: a
                            })}><Link className='link' to="/singlePage">More details</Link></button>
                        </div>
                ))

            }
             </div>



        </div>
    )
}

export default Home
