import React from 'react'
import { useState, useEffect } from 'react'
import { useSelector, useDispatch } from "react-redux"
import { Link } from "react-router-dom";

import axios from 'axios';



function Home() {
    const [name, setName] = useState('')
    const [movie, setMovie] = useState("")



    const selector = useSelector(state => state.allMovieReducer)
    const dispatch = useDispatch()


    const handleSubmit = (e) => {
        e.preventDefault()
        setMovie(name)
    }


   // const SearchApi =`http://www.omdbapi.com/?i=${id}&apikey=34992f21`


        useEffect(() => {
            axios.get(`http://www.omdbapi.com/?s=${name}&apikey=34992f21`)
                .then(res => res.data)
                .then(response => dispatch({
                    type: "data",
                    payload: response.Search
                }))

        }, [movie])


        const handleFavorite=(a)=>{

            dispatch({
                type: "favorite",
                payload: a
              })

        }




    return (
        <div>
            <div className='main'>
                <form onSubmit={handleSubmit}>
                    <h1>Search for movie, TV series ..</h1>
                    <input type="text" className='searchInput' value={name} onChange={(e) => setName(e.target.value)} />
                    <button className='btn' >Search</button>

                </form>
            </div>

            <div className="Movie_main">
                {

                    selector[1] && selector[1].map((a, b) => (
                        <div key={b} className="movie-main-inside">
                            <Link to="singlePage"> <img src={a.Poster} onClick={() => dispatch({
                                type: "moreDetail",
                                payload: a
                            })} /> </Link>
                            <button className='more-details' onClick={() => dispatch({
                                type: "moreDetail",
                                payload: a
                            })}><Link className='link' to="/singlePage">More details</Link></button>

                            <button  onClick={() => handleFavorite(a)}>Add to favorite</button>

                        </div>
                    ))


                }
            </div>



        </div>
    )
}

export default Home
